sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
	var table = request.pathParams.table
	var params = request.queryParams
	var res = []

	if(table == 'sys_user') {
		new global.GlideQuery(table)
			.where('active', true)
			.where('name','!=',null)
			.where('u_samaccountname','!=',null)
			.select(params.sysparm_fields[0].split(','))
			.limit(params.sysparm_limit)
			.forEach(function (user) {
				var obj = {}

				for(key in user) {
					obj[key] = user[key]
				}

				res.push(obj)
			})
	} else {
		new global.GlideQuery(table)
			.select(params.sysparm_fields[0].split(','))
			.limit(params.sysparm_limit)
			.forEach(function (user) {
				var obj = {}

				for(key in user) {
					obj[key] = user[key]
				}

				res.push(obj)
			})
	}

	response.setContentType('application/json')
	response.setStatus(200)
	response.getStreamWriter().writeString(JSON.stringify(res))
})(request, response);
